import React from 'react';
import DefaultView from './component/DefaultView.js';
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props);
            this.state = { movies: [] };
          this.state.favorites = [] ;

    }

    async componentDidMount() {
        try{
            const url = "http://www.randyconnolly.com/funwebdev/3rd/api/movie/movies-brief.php?id=ALL";

            const response = await fetch(url);
            const jsonData = await response.json();
            this.setState( {movies: jsonData});

        } catch (error) {
            console.error(error);
        }
        console.dir(this.state.movies);
    }

     addToFavorite = (poster) => {
        const data = this.state.favorites;
        data.push(poster);
        this.setState({favorites: data});
    }



  render(){
       return (
        <main>

            <DefaultView movies={this.state.movies} addsFav={this.addToFavorite} />

        </main>
    );
  }

}

export default App;
