import React from 'react';
import MovieThumb from './MovieThumb.js';
import Filter from './Filter.js';
import './MovieList.css';
import MovieDetailsView from './MovieDetailsView.js';
import { Route } from 'react-router-dom';
import { Link } from 'react-router-dom';


class MovieList extends React.Component {
    render() {

        if (this.props.loaded) {
            return (
                <article className="FiltersWithMovie">

                    <div className="movieFilter">
                        <h1>Movie Filter</h1>
                        <Filter />
                    </div>

                    <div className="MovieList">
                        <h1 className="movieTitle"> Movies </h1>

                        <div className="listBox">
                            {this.props.movies.map((m, index) =>
                                <MovieThumb key={index} movie={m} id={m.id} title={m.title} year={m.release_date} rating={m.ratings.average} addFav={this.props.addFav} />)}
                        </div>
                    </div>
                </article>
            );
        }
        else {
            return (<span><i className="fas fa-spinner fa-spin"></i></span>);
        }
    }
}

export default MovieList;
