import React from 'react';
import MovieDetails from './MovieDetails';
import HeaderApp from './HeaderApp.js';
import DetailTabs from './DetailTabs.js';

class MovieDetailsView extends React.Component {

    constructor(props) {
        super(props);
        this.state = { id: "", loaded: false };
    }

    componentDidMount() {
        const { clickedId } = this.props.match.params;
        this.setState({ id: clickedId, loaded: true })
    }

    render() {
        if (this.state.loaded) {
            return (
                <div>
                    <HeaderApp />
                    {/* <Favorites /> */}
                    <MovieDetails id={this.state.id} />
                    {/* <DetailTabs /> */}
                </div>
            )
        }
        else {
            return <div></div>;
        }
    }


}



export default MovieDetailsView;
