import React from "react";
import { Link } from 'react-router-dom';

class MovieDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = { movieData: {} };
    }

    async componentDidMount() {
        try {
            const url = `http://www.randyconnolly.com/funwebdev/3rd/api/movie/movies.php?id=1144`;

            const response = await fetch(url);
            const jsonData = await response.json();

            this.setState({ movieData: jsonData });


        } catch (error) {
            console.error(error);
        }
        console.log(this.state.movieData)
    }

    render() {
        const imgUrl = `https://image.tmdb.org/t/p/w185/${this.state.movieData.poster}`;

        return (
            <div>
                <h1>{this.state.movieData.title}</h1>
                <figure>
                    <img id="poster" src={imgUrl} />
                </figure>
                <div id="details-details">

                    {this.state.movieData.release_data},
                    {this.state.movieData.revenue},
                    {this.state.movieData.runtime},
                    {this.state.movieData.tagline},
                    {/* <a href={"https://www.themoviedb.org/movie/" + this.state.movieData.tmdb_id}>TMDB</a>,
                    <a href={"https://www.imdb.com/title" + this.state.movieData.imdb_id}>IMDB</a>, */}
                    {console.log(this.state.movieData)}
                    {/* {this.state.movieData.details.overview}, */}
                    {/* {this.state.data.ratings.popularity},
                    {this.state.data.ratings.average},
                    {this.state.data.ratings.count} */}


                </div>
                <div id="details-companies"></div>
                <div id="details-keywords"></div>
                <div id="details-genres"></div>
            </div>
        );
    }

}


export default MovieDetails;
