    constructor(props) {
        super(props);
        this.state = { movies: this.props.movies };
    }

    addFav = (poster) => {
        this.props.addsFav(poster);
    }

    filterMovie = (title, year, rating) => {
        console.log("Filters choosen: " + title + " " + year + " " + rating);

        let startingList = this.state.movies;
        console.log(startingList);

        // filter movie by title
        const filteredTitle = startingList.filter((movie) => {
            if ((movie.title.toLowerCase()).includes(title.toLowerCase())) {
                return movie;
            }
        });

