import React from 'react';
import MovieList from './MovieList.js';
import Header from './HeaderApp.js';
import * as cloneDeep from 'lodash/cloneDeep';

import FilteredMovieList from './FilteredMovieList.js';
import Favorites from './Favorites.js';

class DefaultView extends React.Component {
    constructor(props) {
        super(props);
        // this.state = { movie: this.props.movies, filteredMovies: [] };
        // this.state.movies = this.props.movies;
        this.state = { movies: this.sortAlphabet(this.props.movies), loaded: false };
        this.state = { title: "", yearBefore: "", ratingBefore: "" };
        this.state.filteredMovies = {};
        this.state.showFiltered = false;
        this.state.filterPoster = [];

        console.log("c");
    }

    addFav = (poster) => {
        this.props.addsFav(poster);

        let post = cloneDeep(this.state.filterPoster);

        let tempList  = [];

        tempList.push(poster);

        post = cloneDeep(tempList);

        console.log(post);


    }


    filterMovie = (title, minYear, maxYear, minRating, maxRating) => {

        let initialList = this.props.movies;
        let sortedTitle = [];
        let sortedRating = [];
        let sortedYear = [];

        this.setState({ movies: initialList })
        if (title) {
            sortedTitle = initialList.filter((movie) => {
                if ((movie.title.toLowerCase()).includes(title.toLowerCase())) {
                    return movie;
                }
            }
            )
        } else { sortedTitle = initialList }

        if (minRating && maxRating) {
            sortedRating = sortedTitle.filter((movie) => {
                if (minRating <= movie.ratings.average && movie.ratings.average <= maxRating) {
                    return movie;
                }
            })
        }
        else if (minRating) {
            sortedRating = sortedTitle.filter((movie) => {
                if (minRating <= movie.ratings.average) {
                    return movie;
                }
            })
        }
        else if (maxRating) {
            sortedRating = sortedTitle.filter((movie) => {
                if (movie.ratings.average <= maxRating) {
                    return movie;
                }
            })
        }
        if (sortedRating.length == 0) { sortedRating = sortedTitle }

        if (minYear && maxYear) {
            sortedYear = sortedRating.filter((movie) => {
                if (minYear <= movie.release_date.substring(0, 4) && movie.release_date.substring(0, 4) <= maxYear) {
                    return movie;
                }
            })
        }
        else if (minYear) {
            sortedYear = sortedRating.filter((movie) => {
                if (minYear <= movie.release_date.substring(0, 4)) {
                    return movie;
                }
            })
        }
        else if (maxYear) {
            sortedYear = sortedRating.filter((movie) => {
                if (movie.release_date.substring(0, 4) <= maxYear) {
                    return movie;
                }
            })
        }
        if (sortedYear.length == 0) { sortedYear = sortedRating }
        this.setState({ filteredMovies: sortedYear });
        this.setState({ showFiltered: true });

    }

    clearFilter = () => {
        this.setState({ filteredMovies: {}, showFiltered: false });
    }

    sortAlphabet = (movieArray) => {
        movieArray.sort((a, b) => {
            var x = a.title.toLowerCase();
            var y = b.title.toLowerCase();
            if (x < y) { return -1; }
            if (x > y) { return 1; }
            return 0;
        })
        // this.setState({ movie: movieArray });
        return movieArray;
    }

    // sortYear = (movieArray) => {
    //     movieArray.sort((a, b) => {
    //         var x = a.title.toLowerCase();
    //         var y = b.title.toLowerCase();
    //         if (x < y) { return -1; }
    //         if (x > y) { return 1; }
    //         return 0;
    //     })
    //     return movieArray;
    // }

    sortRating = (movieArray) => {
        console.log("sort rating");
        movieArray.sort((a, b) => {
            var x = a.ratings.average;
            var y = b.ratings.average;
            if (x < y) { return -1; }
            if (x > y) { return 1; }
            return 0;
        })
        // this.setState({ movie: movieArray });
        // return movieArray;
    }

    render() {
        if (this.props.loaded) {
            if (!this.state.showFiltered) {
                return (
                    <section>
                        <Header />
                        <Favorites favs={this.props.favsList} />

                        <MovieList loaded={this.props.loaded} favsList={this.props.favsList} movies={this.props.movies} addFav={this.addFav} filterMovie={this.filterMovie} clearFilter={this.clearFilter} />
                    </section>
                );
            }
            else {
                return (
                    <section>
                        <Header />
                        <Favorites favs={this.props.favsList} />
                        <FilteredMovieList loaded={this.props.loaded} movies={this.state.filteredMovies} title={this.state.title} yearBefore={this.state.yearBefore} ratingBefore={this.state.ratingBefore} addFav={this.addFav} filterMovie={this.filterMovie} clearFilter={this.clearFilter} />

                    </section >
                );
            }
        } else {
            return (<span><i className="fas fa-spinner fa-spin"></i></span>);
        }
    }


}

export default DefaultView;
