import React from 'react';
import * as cloneDeep from 'lodash/cloneDeep';
import DefaultView from './component/DefaultView.js';
import Favorites from './component/Favorites.js';
import './App.css';
import Home from "./component/Home.js";
import MovieDetailsView from "./component/MovieDetailsView.js";
import { Route } from 'react-router-dom';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { movies: [] };
    this.state.favorites = [];
    this.state.loaded = false;
  }

  async componentDidMount() {
    try {
      const url = "http://www.randyconnolly.com/funwebdev/3rd/api/movie/movies-brief.php?id=ALL";

      const response = await fetch(url);
      const jsonData = await response.json();
      this.setState({ movies: jsonData });
      this.setState({ loaded: true });

    } catch (error) {
      console.error(error);
    }
    console.dir(this.state.movies);
  }

  addToFavorite = (poster) => {
    const data = this.state.favorites;
    data.push(poster);
    console.log({ data });
    this.setState({ favorites: data });
  }

  render() {
    return (
      <main>
        {/* <DefaultView movies={this.state.movies} /> */}
        {/* <Route path='/moviedetails' exact component={MovieDetailsView} /> */}

        <Route path="/" exact component={Home} />
        <Route path="/default" exact render={() =>
          <DefaultView loaded={this.state.loaded} movies={this.state.movies} addsFav={this.addToFavorite} />
        } />
        <Route path="/moviedetails/:clickedId" exact component={MovieDetailsView} />

        {/* <DefaultView movies={this.state.movies} addsFav={this.addToFavorite} /> */}
        {/* <Favorites favs={this.state.favorites} /> */}
      </main>
    );
  }

}

export default App;
