import React from 'react';
// import MovieList from './MovieList.js';
import FavoriteItem from './FavoriteItem.js';
// import { findByLabelText } from '@testing-library/react';

class Favorites extends React.Component {
    constructor(props){
        super(props);
            this.state = {poster: []};
    }

    render() {
        // if (this.props.favs.length > 0) {
        let post = [];

//        console.log(this.props.favs);

        post.push(this.props.favs);


           for(let c of post)
            {
                if(c === this.props.favs)
                    {
                        console.log("movie");
                    }
                else{
                    console.log(this.state.poster);
                }
            }









        return (
            <div className="favorites" style={{
                display: 'flex',
                flexFlow: 'row wrap',
                padding: '0.7em',
                alignContent: 'center',
                background: 'var(--light-box-background)',
            }
            }>


                <h2>Favorites</h2>
                {
                    this.props.favs.map((p, index) => <FavoriteItem key={index} poster={p}></FavoriteItem>)

                }

            </div >
        )
    }
}

export default Favorites;
