import React from "react";
import { Link } from 'react-router-dom';
import "./MovieDetails.css";
import Ratings from './Ratings.js';

class MovieDetails extends React.Component {
    constructor(props) {
        super(props);
        this.state = { movieData: {}, loaded: false };
    }

    async componentDidMount() {
        try {
            const url = `http://www.randyconnolly.com/funwebdev/3rd/api/movie/movies.php?id=1144`;

            const response = await fetch(url);
            const jsonData = await response.json();

            this.setState({ movieData: jsonData, loaded: true });

        } catch (error) {
            console.error(error);
        }
    }

    renderNormal() {
        const imgUrl = `https://image.tmdb.org/t/p/w500/${this.state.movieData.poster}`;

        return (
            <div id="movie-details">
                <div id="movie-details-column-1">
                    <h1>{this.state.movieData.title}</h1>
                    <figure id="poster">
                        <img src={imgUrl} />
                        {this.state.movieData.tagline} ({this.state.movieData.runtime} mins)
                    </figure>
                    <div id="details-caption">
                    </div>
                </div>
                <div id="movie-details-column-2">
                    <button>Add To Favorites</button>
                    <div id="details-details">
                        It was released in {this.state.movieData.release_date} and had a revenue of ${this.state.movieData.revenue}.<br />
                        <a href={"https://www.themoviedb.org/movie/" + this.state.movieData.tmdb_id}>TMDB</a> <a href={"https://www.imdb.com/title" + this.state.movieData.imdb_id}>IMDB</a><br />
                        {this.state.movieData.details.overview}<br />
                        <b>Popularity: </b>{this.state.movieData.ratings.popularity}<br />
                        <b>Average Rating: </b><Ratings rating={this.state.movieData.ratings.average} /><br />
                        <b>Rating Count: </b>{this.state.movieData.ratings.count}
                    </div>

                    Companies
                    <div id="details-companies">
                        {this.state.movieData.production.companies.map((company, key) => {
                            return <div key={key} id="company">{company.name}</div>;
                        })}
                    </div>

                    Countries
                    <div id="details-countries">
                        {this.state.movieData.production.countries.map((country, key) => {
                            return <div key={key} id="country">{country.name}</div>;
                        })}
                    </div>

                    Keywords
                    <div id="details-keywords">
                        {this.state.movieData.details.keywords.map((keyword, key) => {
                            return < div key={key} id="keyword">#{keyword.name}</ div>;
                        })}
                    </div>

                    Genre
                    <div id="details-genres">
                        {this.state.movieData.details.genres.map((genre, key) => {
                            return <div key={key} id="genre">{genre.name}</div>;

                        })}
                    </div>
                </div>
            </div >
        );
    }




    render() {
        if (this.state.loaded) {
            return this.renderNormal();
        }
        else {
            return (<span><i className="fas fa-spinner fa-spin"></i></span>);
        }
    }

}


export default MovieDetails;
