import React from 'react';
import MovieThumb from './MovieThumb.js';
import MovieFilter from './MovieFilter.js';
import FilteredMovieThumb from './FilteredMovieThumb.js';
import './MovieList.css';

class FilteredMovieList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { filterMovies: [] };
    }

    render() {

        var movieTitle = this.props.title;
        var ratingBelow = this.props.ratingBelow;
        var ratingAbove = this.props.ratingAbove;
        var yearBefore = this.props.yearBefore;

        // const movieList = this.props.movies.filter(m => ((m.title === movieTitle) || (m.ratings.average <= ratingBelow) || (m.ratings.average >= ratingAbove) || (m.release_date.substring(0, 4) === yearBefore)));

        console.log(yearBefore);



        // if (this.props.movies.length > 0) {
        return (
            <article className="FiltersWithMovie">

                <div className="movieFilter">
                    <h1>Movie Filter</h1>
                    <MovieFilter filterMovie={this.props.filterMovie} />

                </div>

                <div className="MovieList">
                    <h1 className="movieTitle"> Movies </h1>
                    <div className="listBox">
                        {this.props.movies.map((m, index) =>
                            <FilteredMovieThumb key={index} movie={m} id={m.id} title={m.title} year={m.release_date} rating={m.ratings.average} addFav={this.props.addFav} />)
                        }
                    </div>

                </div>

            </article>

        );
    }
}

export default FilteredMovieList;
